var Generator={init:function(){},generate:function(name){var t=this;var text=[];var customName=!!name;if(!name){name=t.choose(Excuses.names)}
text.push(t.insertName(t.choose(Excuses.hello),name,customName));text.push(t.choose(Excuses.fail));text.push(t.concat(t.choose(Excuses.action),t.choose(Excuses.date)));text.push(t.choose(Excuses.general));return t.concatAll(text);},insertName:function(str,name,customName){var nameComesFirst=/^.+\[name\]/.test(str);name=(nameComesFirst&&!customName)?name.toLowerCase():name;return str.replace('[name]',name);},needComma:function(str){var substring="как";return str.indexOf(substring)===0;},concat:function(str1,str2,separator){if(this.needComma(str2)){return str1+", "+str2;}else{if(separator){return str1+separator+" "+str2;}else{return str1+" "+str2;}}},concatAll:function(arr){var str="";arr.forEach(function(item,i){str+=item+". ";});return str;},choose:function(arr){return arr[getRandom(0,arr.length-1)];}};function getRandom(min,max){return Math.floor(Math.random()*(max-min+1))+min;}

document.addEventListener('DOMContentLoaded',function(){var result=get("result"),name=get("name"),go=get("go");go.addEventListener("click",function(){generate();});document.body.addEventListener("keypress",function(event){if(event.which==13||event.keyCode==13){generate();}});function generate(){result.textContent=Generator.generate(name.value);}});function get(id){return document.getElementById(id);}